import React, { useState } from "react";
import CustomButton from "../../../components/button";
import {
  Card,
  CardContent,
  IconButton,
  InputAdornment,
  InputLabel,
  TextField,
  Typography,
} from "@mui/material";
import "./index.scss";
import { Visibility, VisibilityOff } from "@mui/icons-material";

function LoginPage() {
  const [showPassword, setShowPassword] = useState(false);

  const handleTogglePassword = () => {
    setShowPassword((prevShowPassword) => !prevShowPassword);
  };
  return (
    <Card className="login-card" elevation={0}>
      <CardContent>
        <Typography className="title" variant="h5" component="h2" gutterBottom>
          Masuk
        </Typography>
        <form>
          <TextField
            label="Username"
            variant="outlined"
            fullWidth
            margin="normal"
            className="login-text-field"
          />
          <TextField
            label="Password"
            type={showPassword ? "text" : "password"}
            variant="outlined"
            fullWidth
            size="medium"
            margin="normal"
            className="login-text-field"
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton
                    edge="end"
                    onClick={handleTogglePassword}
                    onMouseDown={(e) => e.preventDefault()} // Prevents the focus event
                  >
                    {showPassword ? (
                      <span className="show-hide-password">Show</span>
                    ) : (
                      <span className="show-hide-password">Hide</span>
                    )}
                  </IconButton>
                </InputAdornment>
              ),
            }}
          />
          <div className="forgot-password">
            <Typography variant="body2" color="textSecondary">
              Lupa Password?
            </Typography>
          </div>
          <CustomButton fullWidth paddingProp={"21px"}>
            Masuk
          </CustomButton>
          <div className="register-link">
            <hr /> {/* Garis pemisah */}
            <Typography variant="body2" color="textSecondary">
              Belum punya akun?{" "}
              <span className="daftar-sekarang">Daftar Sekarang</span>
            </Typography>
          </div>
        </form>
      </CardContent>
    </Card>
  );
}

export default LoginPage;
