// File: CustomButton.js

import React from "react";
import PropTypes from "prop-types";
import { Button } from "@mui/material";
import "./index.scss"; // Buatlah file index.scss untuk mengatur gaya tombol khusus jika diperlukan

function CustomButton({ color, ...props }) {
  const buttonColor = color || "#EB3F36"; // Warna default adalah #EB3F36 jika prop color tidak diberikan

  return (
    <Button
      className="custom-button"
      style={{ backgroundColor: buttonColor }}
      {...props}
    >
      {props.children}
    </Button>
  );
}

CustomButton.propTypes = {
  color: PropTypes.string, // Tipe prop color adalah string
};

export default CustomButton;
