import React, { useState } from "react";
import CustomButton from "../../../components/button";
import {
  Card,
  CardContent,
  Divider,
  IconButton,
  InputAdornment,
  TextField,
  Typography,
} from "@mui/material";
import "./index.scss";
import { ArrowBack } from "@mui/icons-material";

function RegisterPage() {
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [step, setStep] = useState(1); // State untuk mengontrol tampilan form
  const [isStep1Valid, setIsStep1Valid] = useState(false); // State untuk menandakan apakah Step 1 valid

  const handleTogglePassword = () => {
    setShowPassword((prevShowPassword) => !prevShowPassword);
  };

  const handleToggleConfirmPassword = () => {
    setShowConfirmPassword((prevShowPassword) => !prevShowPassword);
  };

  const handleNextStep = () => {
    if (step === 1) {
      // Lakukan validasi sebelum pindah ke step 2
      if (!validateStep1()) {
        return;
      }
      setStep(2);
    }
  };

  const handleBackStep = () => {
    if (step === 2) {
      setStep(1);
    }
  };

  const validateStep1 = () => {
    // Lakukan validasi untuk setiap textfield pada step 1
    let isValid = true;
    if (!formData.firstName.trim()) {
      setFirstNameError(true);
      isValid = false;
    } else {
      setFirstNameError(false);
    }
    if (!formData.lastName.trim()) {
      setLastNameError(true);
      isValid = false;
    } else {
      setLastNameError(false);
    }
    if (!formData.email.trim()) {
      setEmailError(true);
      isValid = false;
    } else {
      setEmailError(false);
    }
    return isValid;
  };

  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    email: "",
  });
  return (
    <Card
      className={`login-card ${step === 2 ? "step-entering" : ""}`}
      elevation={0}
    >
      <CardContent>
        {step === 1 && (
          <Typography
            className="title"
            variant="h5"
            component="h2"
            gutterBottom
          >
            Daftar Sekarang
          </Typography>
        )}
        {step === 2 && (
          <Typography
            className="title"
            variant="h5"
            component="h2"
            gutterBottom
            onClick={handleBackStep}
            style={{ cursor: "pointer" }}
          >
            <ArrowBack /> Kembali
          </Typography>
        )}
        <form>
          {step === 1 && (
            <>
              <TextField
                label="Nama Depan"
                variant="outlined"
                fullWidth
                margin="normal"
                className={`login-text-field ${firstNameError ? "error" : ""}`}
                value={formData.firstName}
                onChange={(e) =>
                  setFormData({ ...formData, firstName: e.target.value })
                }
              />
              <TextField
                label="Nama Belakang"
                variant="outlined"
                fullWidth
                margin="normal"
                className={`login-text-field ${lastNameError ? "error" : ""}`}
                value={formData.lastName}
                onChange={(e) =>
                  setFormData({ ...formData, lastName: e.target.value })
                }
              />
              <TextField
                label="Email"
                variant="outlined"
                fullWidth
                margin="normal"
                className={`login-text-field ${emailError ? "error" : ""}`}
                value={formData.email}
                onChange={(e) =>
                  setFormData({ ...formData, email: e.target.value })
                }
              />
            </>
          )}
          {step === 2 && (
            <>
              <TextField
                label="Nomor Telepon"
                variant="outlined"
                fullWidth
                margin="normal"
                className="login-text-field"
              />
              <TextField
                label="Password"
                type={showPassword ? "text" : "password"}
                variant="outlined"
                fullWidth
                size="medium"
                margin="normal"
                className="login-text-field"
                InputProps={{
                  endAdornment: (
                    <InputAdornment position="end">
                      <IconButton
                        edge="end"
                        onClick={handleTogglePassword}
                        onMouseDown={(e) => e.preventDefault()}
                      >
                        {showPassword ? (
                          <span className="show-hide-password">Show</span>
                        ) : (
                          <span className="show-hide-password">Hide</span>
                        )}
                      </IconButton>
                    </InputAdornment>
                  ),
                }}
              />
              <TextField
                label="Konfirmasi Password"
                type={showConfirmPassword ? "text" : "password"}
                variant="outlined"
                fullWidth
                size="medium"
                margin="normal"
                className="login-text-field"
                InputProps={{
                  endAdornment: (
                    <InputAdornment position="end">
                      <IconButton
                        edge="end"
                        onClick={handleToggleConfirmPassword}
                        onMouseDown={(e) => e.preventDefault()}
                      >
                        {showConfirmPassword ? (
                          <span className="show-hide-password">Show</span>
                        ) : (
                          <span className="show-hide-password">Hide</span>
                        )}
                      </IconButton>
                    </InputAdornment>
                  ),
                }}
              />
            </>
          )}
          <Divider style={{ height: "39px" }} />
          {step === 1 && (
            <CustomButton
              fullWidth
              paddingProp={"21px"}
              onClick={handleNextStep}
            >
              Selanjutnya
            </CustomButton>
          )}
          {step === 2 && (
            <CustomButton
              fullWidth
              paddingProp={"21px"}
              onClick={handleBackStep}
            >
              Kembali
            </CustomButton>
          )}
          <div className="register-link">
            <hr />
            <Typography
              variant="body2"
              className="daftar-sekarang"
              color="textSecondary"
            >
              Sudah punya akun?<span>Masuk</span>
            </Typography>
          </div>
        </form>
      </CardContent>
    </Card>
  );
}

export default RegisterPage;
