import React from "react";
import { Navigate, Route, Routes, useNavigate } from "react-router-dom";
import routesList from "./RoutesList";

const Dashboard = React.lazy(() => import("../pages/dashboard"));
const ProductDetail = React.lazy(() =>
  import("../pages/product/detailProduct")
);
const Login = React.lazy(() => import("../pages/auth/login"));
const Register = React.lazy(() => import("../pages/auth/register"));

const isAuthenticated = () => {
  return !!localStorage.getItem("token");
};

function RoutePath() {
  const navigate = useNavigate();
  if (isAuthenticated()) {
    // Jika pengguna berhasil login, cek apakah ada path sebelumnya yang disimpan di localStorage
    const previousPath = localStorage.getItem("previousPath");
    localStorage.removeItem("previousPath"); // Hapus data path sebelumnya setelah digunakan

    if (previousPath) {
      // Jika ada path sebelumnya, arahkan pengguna kembali ke halaman tersebut
      return <Navigate to={previousPath} replace />;
    } else {
      // Jika tidak ada path sebelumnya, arahkan pengguna ke halaman dashboard (atau halaman default)
      return <Navigate to="/" replace />;
    }
  }
  return (
    <>
      <Routes>
        <Route
          path={routesList.dashboard.root}
          element={
            isAuthenticated() ? <Dashboard /> : <Navigate to="/login" replace />
          }
        />
        <Route path={routesList.auth.login} element={<Login />} />
        <Route path={routesList.auth.register} element={<Register />} />
        <Route
          path={routesList.product.detail}
          element={
            isAuthenticated() ? (
              <ProductDetail />
            ) : (
              <Navigate to="/login" replace />
            )
          }
        />
      </Routes>
    </>
  );
}

export default RoutePath;
