import React, { useState } from "react";
import CustomButton from "../../../components/button";
import {
  Card,
  CardContent,
  IconButton,
  InputAdornment,
  TextField,
  Typography,
} from "@mui/material";
import "./index.scss";

function LoginPage() {
  const [showPassword, setShowPassword] = useState(false);
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [usernameError, setUsernameError] = useState(false);
  const [passwordError, setPasswordError] = useState(false);

  const handleTogglePassword = () => {
    setShowPassword((prevShowPassword) => !prevShowPassword);
  };

  const handleLogin = (e) => {
    e.preventDefault();

    // Validasi "required" untuk textfield "Username" dan "Password"
    if (username.trim() === "") {
      setUsernameError(true);
    } else {
      setUsernameError(false);
    }

    if (password.trim() === "") {
      setPasswordError(true);
    } else {
      setPasswordError(false);
    }

    // Lakukan aksi login jika data valid
    if (username.trim() !== "" && password.trim() !== "") {
      // Lakukan aksi login disini
      console.log("Login successful!");
    }
  };

  return (
    <Card className="login-card" elevation={0}>
      <CardContent>
        <Typography className="title" variant="h5" component="h2" gutterBottom>
          Masuk
        </Typography>
        <form>
          <TextField
            label="Username"
            variant="outlined"
            fullWidth
            margin="normal"
            className={`login-text-field ${usernameError ? "error" : ""}`}
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            error={usernameError}
          />
          {usernameError && (
            <span className="error-text">Username is required</span>
          )}
          <TextField
            label="Password"
            type={showPassword ? "text" : "password"}
            variant="outlined"
            fullWidth
            size="medium"
            margin="normal"
            className={`login-text-field ${passwordError ? "error" : ""}`}
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            error={passwordError}
            InputProps={{
              endAdornment: (
                <InputAdornment position="end">
                  <IconButton
                    edge="end"
                    onClick={handleTogglePassword}
                    onMouseDown={(e) => e.preventDefault()} // Prevents the focus event
                  >
                    {showPassword ? (
                      <span className="show-hide-password">Show</span>
                    ) : (
                      <span className="show-hide-password">Hide</span>
                    )}
                  </IconButton>
                </InputAdornment>
              ),
            }}
          />
          {passwordError && (
            <span className="error-text">Password is required</span>
          )}
          <div className="forgot-password">
            <Typography variant="body2" color="textSecondary">
              Lupa Password?
            </Typography>
          </div>
          <CustomButton fullWidth paddingProp={"21px"} onClick={handleLogin}>
            Masuk
          </CustomButton>
          <div className="register-link">
            <hr />
            <Typography
              variant="body2"
              className="daftar-sekarang"
              color="textSecondary"
            >
              Belum punya akun?<span>Daftar Sekarang</span>
            </Typography>
          </div>
        </form>
      </CardContent>
    </Card>
  );
}

export default LoginPage;
