import React, { useState } from "react";
import CustomButton from "../../../components/button";
import {
  Card,
  CardContent,
  Divider,
  IconButton,
  InputAdornment,
  TextField,
  Typography,
} from "@mui/material";
import "./index.scss";
import { ArrowBack } from "@mui/icons-material";

function RegisterPage() {
  const [showPassword, setShowPassword] = useState(false);
  const [step, setStep] = useState(1); // State untuk mengontrol tampilan form

  const handleTogglePassword = () => {
    setShowPassword((prevShowPassword) => !prevShowPassword);
  };

  const handleNextStep = () => {
    setStep((prevStep) => prevStep + 1);
  };

  const handleBackStep = () => {
    setStep((prevStep) => prevStep - 1);
  };

  const handleStepTransition = (newStep) => {
    // Determine the animation direction
    const isGoingForward = newStep > step;
    const animationClass = isGoingForward ? "step-entering" : "step-exiting";

    // Set the animation class
    document.querySelector(".login-card").classList.add(animationClass);

    // Wait for the animation to finish before changing the step
    setTimeout(() => {
      setStep(newStep);
      document.querySelector(".login-card").classList.remove(animationClass);
    }, 300); // Duration of the animation in milliseconds
  };

  return (
    <Card className={`login-card ${step === 2 ? "step-entering" : ""}`}>
      <CardContent>
        {step === 1 ? (
          <>
            <Typography
              className="title"
              variant="h5"
              component="h2"
              gutterBottom
            >
              Daftar Sekarang
            </Typography>
            <form>{/* ... Form for step 1 ... */}</form>
            <CustomButton
              fullWidth
              paddingProp={"21px"}
              onClick={() => handleStepTransition(2)}
            >
              Selanjutnya
            </CustomButton>
          </>
        ) : step === 2 ? (
          <>
            <Typography
              className="title"
              variant="h5"
              component="h2"
              gutterBottom
              onClick={() => handleStepTransition(1)}
              style={{ cursor: "pointer" }}
            >
              <ArrowBack /> Kembali
            </Typography>
            <form>{/* ... Form for step 2 ... */}</form>
            <CustomButton
              fullWidth
              paddingProp={"21px"}
              onClick={() => handleStepTransition(1)}
            >
              Selesai
            </CustomButton>
          </>
        ) : null}
        <div className="register-link">
          <hr />
          <Typography
            variant="body2"
            className="daftar-sekarang"
            color="textSecondary"
          >
            Sudah punya akun?<span>Masuk</span>
          </Typography>
        </div>
      </CardContent>
    </Card>
  );
}

export default RegisterPage;
