import { Grid, Slider } from "@mui/material";
import React, { useState } from "react";
import "./index.scss";
import { FormatRupiah } from "@arismun/format-rupiah";

function PriceRangeSlider({ open }) {
  const [valueRange, setValueRange] = useState([100000, 1000000]);

  const handleMinPriceChange = (event) => {
    const minPrice = event.target.value;
    setValueRange([minPrice, valueRange[1]]);
  };

  const handleMaxPriceChange = (event) => {
    const maxPrice = event.target.value;
    setValueRange([valueRange[0], maxPrice]);
  };

  return (
    <div className="price-filter">
      <div className="title">Harga</div>
      <div className="range">
        <Slider
          value={valueRange}
          onChange={(e, newValue) => setValueRange(newValue)}
          valueLabelDisplay="auto"
          aria-labelledby="range-slider"
          min={0}
          max={2250000}
          sx={{
            "& .MuiSlider-thumb": {
              color: "#EB3F36", // Ubah warna thumb menjadi warna EB3F36
            },
            "& .MuiSlider-track": {
              color: "#EB3F36", // Ubah warna track menjadi warna EB3F36
            },
            "& .MuiSlider-rail": {
              color: "#757575", // Ubah warna rail menjadi warna EB3F36
            },
          }}
        />
      </div>
      <div className="nominal">
        <Grid container spacing={2}>
          <Grid item>
            <div>Rp.</div>
            <input
              type="number"
              value={FormatRupiah()}
              onChange={handleMinPriceChange}
              min={0}
              max={2250000}
              className="price-input"
            />
          </Grid>
          <Grid item>
            <input
              type="number"
              value={valueRange[1]}
              onChange={handleMaxPriceChange}
              min={0}
              max={2250000}
              className="price-input"
            />
          </Grid>
        </Grid>
      </div>
    </div>
  );
}

export default PriceRangeSlider;
