import React, { useState } from "react";
import AppbarCustom from "../../components/appbar";
import { Container, Grid } from "@mui/material";
import BreadcrumbsCustom from "../../components/breadcrumbs";
import "./index.scss";
import Select from "react-select";

function DashboardPage() {
  const [productsPerPage, setProductsPerPage] = useState("12"); // Atur nilai awal menjadi 12

  const options = [
    { value: 10, label: "10" },
    { value: 20, label: "20" },
    { value: 50, label: "50" },
    { value: 100, label: "100" },
  ];

  const handleProductsPerPageChange = (event) => {
    setProductsPerPage(event.target.value);
  };

  return (
    <div>
      <AppbarCustom />
      <Container style={{ maxWidth: "1300px", paddingTop: "24px" }}>
        <BreadcrumbsCustom data={["Home", "Produk", "Roasted Ban"]} />
        <Grid container spacing={1} paddingTop={"38px"}>
          <Grid item xs={6} md={4}>
            Filter
          </Grid>
          <Grid className="product-content" item xs={6} md={8}>
            <div className="header">
              <div>
                <span>Menampilkan</span>
                <Select
                  options={options}
                  value={productsPerPage}
                  onChange={handleProductsPerPageChange}
                  styles={{
                    control: (provided, state) => ({
                      ...provided,
                      border: "1px solid #F2F2F2",
                      background: "rgba(248, 248, 248, 0.75)",
                      width: "100px",
                      height: "30px",
                      boxShadow: "none",
                      borderRadius: "0",
                      "&:hover": {
                        border: "1px solid #F2F2F2",
                      },
                    }),
                    singleValue: (provided) => ({
                      ...provided,
                      marginRight: "10px",
                    }),
                    menu: (provided) => ({
                      ...provided,
                      width: "50px", // Ubah ukuran dropdown ketika dibuka
                    }),
                  }}
                />
              </div>
            </div>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
}

export default DashboardPage;
