import React from "react";
import { useSelector } from "react-redux";
import AppbarCustom from "../../components/appbar";
import { Container, Grid } from "@mui/material";
import BreadcrumbsCustom from "../../components/breadcrumbs";
import ImageGallery from "react-image-gallery";
import "react-image-gallery/styles/css/image-gallery.css";

function DetailProductPage() {
  const productState = useSelector((state) => state.product);

  const images = [
    {
      original: "https://picsum.photos/id/1018/1000/600/",
      thumbnail: "https://picsum.photos/id/1018/250/150/",
    },
    {
      original: "https://picsum.photos/id/1015/1000/600/",
      thumbnail: "https://picsum.photos/id/1015/250/150/",
    },
    {
      original: "https://picsum.photos/id/1019/1000/600/",
      thumbnail: "https://picsum.photos/id/1019/250/150/",
    },
  ];

  return (
    <div>
      <AppbarCustom />
      <Container style={{ maxWidth: "1300px", paddingTop: "24px" }}>
        <BreadcrumbsCustom
          data={[
            "Home",
            productState?.productDetail?.data?.list[0].name
              .replace(/\\/g, "")
              .replace(/"/g, "")
              .toUpperCase(),
          ]}
        />
        <Grid container spacing={1} paddingTop={"38px"}>
          <Grid item>
            <ImageGallery
              items={images}
              showNav={false} // Hide the default navigation arrows
              showFullscreenButton={false} // Hide the fullscreen button
              showPlayButton={false} // Hide the play/pause button
              showBullets // Show bullets for each image
              onClick={(e) => handleImageSelect(e.target.alt)}
            />
          </Grid>
          <Grid item></Grid>
        </Grid>
      </Container>
    </div>
  );
}

export default DetailProductPage;
