import { Grid, Slider } from "@mui/material";
import React, { useState } from "react";
import "./index.scss";

function PriceRangeSlider() {
  const [valueRange, setValueRange] = useState([100000, 1000000]);

  const handleMinPriceChange = (event) => {
    const minPrice = parseInt(event.target.value);
    setValueRange([minPrice, valueRange[1]]);
  };

  const handleMaxPriceChange = (event) => {
    const maxPrice = parseInt(event.target.value);
    setValueRange([valueRange[0], maxPrice]);
  };

  function formatRupiah(angka, prefix = "Rp. ") {
    var number_string = angka?.replace(/[^,\d]/g, "").toString(),
      split = number_string.split(","),
      sisa = split[0].length % 3,
      rupiah = split[0].substr(0, sisa),
      ribuan = split[0].substr(sisa).match(/\d{3}/g);

    if (ribuan) {
      let separator = sisa ? "." : "";
      rupiah += separator + ribuan.join(".");
    }

    rupiah = split[1] !== undefined ? rupiah + "," + split[1] : rupiah;
    return prefix + rupiah;
  }

  return (
    <div className="price-filter">
      <div className="title">Harga</div>
      <div className="range">
        <Slider
          value={valueRange}
          onChange={(e, newValue) => setValueRange(newValue)}
          valueLabelDisplay="auto"
          aria-labelledby="range-slider"
          min={0}
          max={2250000}
          sx={{
            "& .MuiSlider-thumb": {
              color: "#EB3F36", // Ubah warna thumb menjadi warna EB3F36
            },
            "& .MuiSlider-track": {
              color: "#EB3F36", // Ubah warna track menjadi warna EB3F36
            },
            "& .MuiSlider-rail": {
              color: "#757575", // Ubah warna rail menjadi warna EB3F36
            },
          }}
        />
      </div>
      <div className="nominal">
        <Grid container spacing={2}>
          <Grid item display={"flex"} alignItems={"center"} gap={"10px"}>
            <div>Rp. </div>
            <input
              type="number"
              value={valueRange[0]}
              onChange={handleMinPriceChange}
              min={0}
              max={2250000}
              className="price-input"
            />
          </Grid>
          <Grid item display={"flex"} alignItems={"center"} gap={"10px"}>
            <div>Rp. </div>
            <input
              type="number"
              value={formatRupiah(valueRange[1], "")}
              onChange={handleMaxPriceChange}
              min={0}
              max={2250000}
              className="price-input"
            />
          </Grid>
        </Grid>
      </div>
    </div>
  );
}

export default PriceRangeSlider;
