import React from "react";
import CustomButton from "../../../components/button";
import {
  Card,
  CardContent,
  TextField,
  Typography,
  makeStyles,
} from "@mui/material";
import "./index.scss";

const useStyles = makeStyles((theme) => ({
  loginTextField: {
    // Gaya untuk TextField
    width: "100%",
    height: "59px",
    borderRadius: "7px",
    background: "#FFF",
    boxShadow: "0px 1px 4px 0px rgba(0, 0, 0, 0.25)",
    margin: "10px 0",
    padding: "10px",
    border: "1px solid #ccc",
    outline: "none",
    "&:focus": {
      border: "2px solid #EB3F36",
    },
  },
}));

function LoginPage() {
  const classes = useStyles();
  return (
    <Card className="login-card" elevation={0}>
      <CardContent>
        <Typography variant="h5" component="h2" gutterBottom>
          Login
        </Typography>
        <form>
          <TextField
            label="Username"
            variant="outlined"
            fullWidth
            margin="normal"
            className="login-text-field" // Gunakan kelas gaya yang baru saja dibuat
          />
          <TextField
            label="Password"
            type="password"
            variant="outlined"
            fullWidth
            margin="normal"
            className={classes.loginTextField} // Gunakan kelas gaya yang baru saja dibuat
          />
          <CustomButton fullWidth>Masuk</CustomButton>
        </form>
      </CardContent>
    </Card>
  );
}

export default LoginPage;
