import { Grid, Slider } from "@mui/material";
import React, { useState } from "react";
import "./index.scss";

function PriceRangeSlider({ data = [100000, 1000000] }) {
  const [valueRange, setValueRange] = useState(data);

  const handleMinPriceChange = (event) => {
    const minPrice = parseInt(
      event.target.value.replace(/[Rp. ]/g, "").replace(/\./g, "")
    );
    setValueRange([minPrice, valueRange[1]]);
  };

  const handleMaxPriceChange = (event) => {
    const maxPrice = parseInt(
      event.target.value.replace(/[Rp. ]/g, "").replace(/\./g, "")
    );
    setValueRange([valueRange[0], maxPrice]);
  };

  const formatRupiah = (angka) => {
    let number_string = angka.toString().replace(/\D/g, "");
    let split = number_string.split("");
    let sisa = split.length % 3;
    let rupiah = split.slice(0, sisa).join("");
    let ribuan = split.slice(sisa).join("").match(/\d{3}/g);

    if (ribuan) {
      let separator = sisa ? "." : "";
      rupiah += separator + ribuan.join(".");
    }

    return rupiah;
  };

  return (
    <div className="price-filter">
      <div className="title">Harga</div>
      <div className="range">
        <Slider
          value={valueRange}
          onChange={(e, newValue) => setValueRange(newValue)}
          valueLabelDisplay="auto"
          aria-labelledby="range-slider"
          min={0}
          max={2250000}
          sx={{
            "& .MuiSlider-thumb": {
              color: "#EB3F36", // Ubah warna thumb menjadi warna EB3F36
            },
            "& .MuiSlider-track": {
              color: "#EB3F36", // Ubah warna track menjadi warna EB3F36
            },
            "& .MuiSlider-rail": {
              color: "#757575", // Ubah warna rail menjadi warna EB3F36
            },
          }}
        />
      </div>
      <div className="nominal">
        <Grid container spacing={2}>
          <Grid item display={"flex"} alignItems={"center"} gap={"10px"}>
            <div>Rp. </div>
            <input
              type="text"
              value={formatRupiah(valueRange[0])}
              onChange={handleMinPriceChange}
              className="price-input"
            />
          </Grid>
          <Grid item display={"flex"} alignItems={"center"} gap={"10px"}>
            <div>Rp. </div>
            <input
              type="text"
              value={formatRupiah(valueRange[1])}
              onChange={handleMaxPriceChange}
              className="price-input"
            />
          </Grid>
        </Grid>
      </div>
    </div>
  );
}

export default PriceRangeSlider;
