import React, { useState } from "react";
import AppbarCustom from "../../components/appbar";
import { Container, Grid } from "@mui/material";
import BreadcrumbsCustom from "../../components/breadcrumbs";

import "./index.scss";
import Select from "react-select";
import ProductCard from "../../components/productCard";

function DashboardPage() {
  const [productsPerPage, setProductsPerPage] = useState("12"); // Atur nilai awal menjadi 12

  const options = [
    { value: 10, label: "10" },
    { value: 20, label: "20" },
    { value: 50, label: "50" },
    { value: 100, label: "100" },
  ];

  const optionsProduct = [{ value: "Nama Product", label: "Nama Product" }];

  return (
    <div>
      <AppbarCustom />
      <Container style={{ maxWidth: "1300px", paddingTop: "24px" }}>
        <BreadcrumbsCustom data={["Home", "Produk", "Roasted Ban"]} />
        <Grid container spacing={1} paddingTop={"38px"}>
          <Grid item xs={6} md={3}>
            Filter
          </Grid>
          <Grid className="product-content" item xs={6} md={9}>
            <div className="header">
              <div style={{ display: "flex", alignItems: "center" }}>
                <div style={{ marginRight: "10px" }}>Menampilkan</div>
                <Select
                  options={options}
                  value={options.find(
                    (option) => option.value === parseInt(productsPerPage)
                  )}
                  defaultValue={options[0]}
                  onChange={(selectedOption) =>
                    setProductsPerPage(selectedOption.value.toString())
                  }
                  styles={{
                    control: (provided, state) => ({
                      ...provided,
                      border: "1px solid #F2F2F2",
                      background: "rgba(248, 248, 248, 0.75)",
                      width: "100px",
                      height: "30px",
                      boxShadow: "none",
                      borderRadius: "0",
                      "&:hover": {
                        border: "1px solid #F2F2F2",
                      },
                    }),
                    singleValue: (provided) => ({
                      ...provided,
                      marginRight: "10px",
                      color: "#696969",
                    }),
                    menu: (provided) => ({
                      ...provided,
                      width: "50px", // Ubah ukuran dropdown ketika dibuka
                    }),
                  }}
                />
                <div style={{ marginLeft: "10px" }}>dari 132</div>
              </div>
              <div style={{ display: "flex", alignItems: "center" }}>
                <div style={{ marginRight: "10px" }}>Urutkan</div>
                <Select
                  options={optionsProduct}
                  value={optionsProduct}
                  defaultValue={optionsProduct[0]}
                  onChange={(selectedOption) =>
                    setProductsPerPage(selectedOption.value.toString())
                  }
                  styles={{
                    control: (provided, state) => ({
                      ...provided,
                      border: "1px solid #F2F2F2",
                      background: "rgba(248, 248, 248, 0.75)",
                      width: "200px",
                      height: "30px",
                      boxShadow: "none",
                      borderRadius: "0",
                      "&:hover": {
                        border: "1px solid #F2F2F2",
                      },
                    }),
                    singleValue: (provided) => ({
                      ...provided,
                      marginRight: "10px",
                      color: "#696969",
                    }),
                    menu: (provided) => ({
                      ...provided,
                      width: "200px", // Ubah ukuran dropdown ketika dibuka
                    }),
                  }}
                />
              </div>
            </div>
            <div className="list-product">
              <ProductCard />
              <ProductCard />
              <ProductCard />
              <ProductCard />
            </div>
          </Grid>
        </Grid>
      </Container>
    </div>
  );
}

export default DashboardPage;
