import React, { useState } from "react";
import CustomButton from "../../../components/button";
import {
  Card,
  CardContent,
  Divider,
  IconButton,
  InputAdornment,
  TextField,
  Typography,
} from "@mui/material";
import "./index.scss";
import { ArrowBack } from "@mui/icons-material";

function RegisterPage() {
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [step, setStep] = useState(1); // State untuk mengontrol tampilan form
  const [isStep1Valid, setIsStep1Valid] = useState(false); // State untuk menandakan apakah Step 1 valid

  const handleTogglePassword = () => {
    setShowPassword((prevShowPassword) => !prevShowPassword);
  };

  const handleToggleConfirmPassword = () => {
    setShowConfirmPassword((prevShowPassword) => !prevShowPassword);
  };

  const handleNextStep = () => {
    if (step === 1) {
      // Cek apakah semua field pada Step 1 telah diisi
      const step1Fields = document.querySelectorAll(".step-1-field");
      let isValid = true;
      step1Fields.forEach((field) => {
        if (!field.value) {
          isValid = false;
          field.classList.add("invalid"); // Tambahkan kelas invalid jika field belum diisi
        } else {
          field.classList.remove("invalid");
        }
      });

      if (isValid) {
        setStep(2);
        setIsStep1Valid(true);
      } else {
        setIsStep1Valid(false);
      }
    }
  };

  const handleBackStep = () => {
    if (step === 2) {
      setStep(1);
    }
  };

  return (
    <Card
      className={`login-card ${step === 2 ? "step-entering" : ""}`}
      elevation={0}
    >
      <CardContent>
        {step === 1 && (
          <Typography
            className="title"
            variant="h5"
            component="h2"
            gutterBottom
          >
            Daftar Sekarang
          </Typography>
        )}
        {step === 2 && (
          <Typography
            className="title"
            variant="h5"
            component="h2"
            gutterBottom
            onClick={handleBackStep}
            style={{ cursor: "pointer" }}
          >
            <ArrowBack /> Kembali
          </Typography>
        )}
        <form>
          {step === 1 && (
            <>
              <TextField
                label="Nama Depan"
                variant="outlined"
                fullWidth
                margin="normal"
                className={`login-text-field step-1-field ${
                  isStep1Valid ? "" : "invalid" // Tambahkan kelas invalid jika form belum valid
                }`}
              />
              <TextField
                label="Nama Belakang"
                variant="outlined"
                fullWidth
                margin="normal"
                className={`login-text-field step-1-field ${
                  isStep1Valid ? "" : "invalid" // Tambahkan kelas invalid jika form belum valid
                }`}
              />
              <TextField
                label="Email"
                variant="outlined"
                fullWidth
                margin="normal"
                className={`login-text-field step-1-field ${
                  isStep1Valid ? "" : "invalid" // Tambahkan kelas invalid jika form belum valid
                }`}
              />
            </>
          )}
          {step === 2 && (
            <>
              <TextField
                label="Nomor Telepon"
                variant="outlined"
                fullWidth
                margin="normal"
                className="login-text-field"
              />
              <TextField
                label="Password"
                type={showPassword ? "text" : "password"}
                variant="outlined"
                fullWidth
                size="medium"
                margin="normal"
                className="login-text-field"
                InputProps={{
                  endAdornment: (
                    <InputAdornment position="end">
                      <IconButton
                        edge="end"
                        onClick={handleTogglePassword}
                        onMouseDown={(e) => e.preventDefault()}
                      >
                        {showPassword ? (
                          <span className="show-hide-password">Show</span>
                        ) : (
                          <span className="show-hide-password">Hide</span>
                        )}
                      </IconButton>
                    </InputAdornment>
                  ),
                }}
              />
              <TextField
                label="Konfirmasi Password"
                type={showConfirmPassword ? "text" : "password"}
                variant="outlined"
                fullWidth
                size="medium"
                margin="normal"
                className="login-text-field"
                InputProps={{
                  endAdornment: (
                    <InputAdornment position="end">
                      <IconButton
                        edge="end"
                        onClick={handleToggleConfirmPassword}
                        onMouseDown={(e) => e.preventDefault()}
                      >
                        {showConfirmPassword ? (
                          <span className="show-hide-password">Show</span>
                        ) : (
                          <span className="show-hide-password">Hide</span>
                        )}
                      </IconButton>
                    </InputAdornment>
                  ),
                }}
              />
            </>
          )}
          <Divider style={{ height: "39px" }} />
          {step === 1 && (
            <CustomButton
              fullWidth
              paddingProp={"21px"}
              onClick={handleNextStep}
            >
              Selanjutnya
            </CustomButton>
          )}
          {step === 2 && (
            <CustomButton
              fullWidth
              paddingProp={"21px"}
              onClick={handleBackStep}
            >
              Kembali
            </CustomButton>
          )}
          <div className="register-link">
            <hr />
            <Typography
              variant="body2"
              className="daftar-sekarang"
              color="textSecondary"
            >
              Sudah punya akun?<span>Masuk</span>
            </Typography>
          </div>
        </form>
      </CardContent>
    </Card>
  );
}

export default RegisterPage;
